import { exec } from 'child_process';
import * as log from 'loglevel';
import { Client } from 'pg';
import { Ident } from 'provide-js';
import { AuthService } from 'ts-natsutil';
import { ParticipantStack } from '../src/index';

export const promisedTimeout = (ms) => {
  return new Promise(resolve => setTimeout(resolve, ms));
};

export const authenticateUser = async (identHost, email, password) => {
  const auth = await Ident.authenticate({
    email: email,
    password: password,
  }, 'http', identHost);
  return auth;
};

export const baselineAppFactory = async (
  orgName,
  bearerToken,
  initiator,
  identHost,
  natsHost,
  natsPrivateKey,
  natsPublicKey,
  nchainHost,
  networkId,
  vaultHost,
  rcpEndpoint,
  rpcScheme,
  workgroup,
  workgroupName,
  workgroupToken,
): Promise<ParticipantStack> => {
  const natsConfig = {
    bearerToken: '',
    natsServers: [natsHost],
    privateKey: natsPrivateKey,
    publicKey: natsPublicKey,
  };
  natsConfig.bearerToken = await vendNatsAuthorization(natsConfig, 'baseline.inbound');

  return new ParticipantStack(
    {
      identApiScheme: 'http',
      identApiHost: identHost,
      initiator: initiator,
      nchainApiScheme: 'http',
      nchainApiHost: nchainHost,
      networkId: networkId, // FIXME-- boostrap network genesis if no public testnet faucet is configured...
      orgName: orgName,
      rpcEndpoint: rcpEndpoint,
      rpcScheme: rpcScheme,
      token: bearerToken,
      vaultApiScheme: 'http',
      vaultApiHost: vaultHost,
      workgroup: workgroup,
      workgroupName: workgroupName,
      workgroupToken: workgroupToken,
    },
    natsConfig,
  );
};

// subsidize all transactions (value + gas) broadcast through the NChain gateway
// ask a maintainer for a valid address/private key to demystify this configuration...
export const configureRopstenFaucet = async (port, userId, address, encryptedPrivateKey) => {
  const identPgclient = new Client({
    user: 'ident',
    host: '0.0.0.0',
    database: 'ident_dev',
    password: 'ident',
    port: port,
  });

  const nchainPgclient = new Client({
    user: 'nchain',
    host: '0.0.0.0',
    database: 'nchain_dev',
    password: 'nchain',
    port: port,
  });

  try {
    await identPgclient.connect();
    await nchainPgclient.connect();

    await identPgclient.query(`INSERT INTO applications (id, created_at, name, user_id, network_id, config, encrypted_config) values ('146ab73e-b2eb-4386-8c6f-93663792c741', now(), 'Ropsten Faucet', '${userId}', '66d44f30-9092-4182-a3c4-bc02736d6ae5', '{}', '\\');`);
    await nchainPgclient.query(`INSERT INTO accounts (id, created_at, application_id, network_id, address, private_key) values ('1be0f75c-c05d-42d7-85fd-0c406466a95c', now(), '146ab73e-b2eb-4386-8c6f-93663792c741', '66d44f30-9092-4182-a3c4-bc02736d6ae5', '${address}', '${encryptedPrivateKey}');`);

  } finally {
    await nchainPgclient.query(`UPDATE networks SET enabled = true WHERE id = '66d44f30-9092-4182-a3c4-bc02736d6ae5'`);
    await identPgclient.end();
    await nchainPgclient.end();

    return true;
  }
};

export const createUser = async (identHost, firstName, lastName, email, password) => {
  const user = await Ident.createUser({
    first_name: firstName,
    last_name: lastName,
    email: email,
    password: password,
  }, 'http', identHost);
  return user;
};

export const scrapeInvitationToken = async (container) => {
  let logs;
  exec(`docker logs ${container}`, (err, stdout, stderr) => {
   logs = stderr.toString();
  });
  await promisedTimeout(500);
  const matches = logs.match(/\"dispatch invitation\: (.*)\"/);
  if (matches && matches.length > 0) {
    return matches[matches.length - 1];
  }
  return null;
};

export const vendNatsAuthorization = async (natsConfig, subject): Promise<string> => {
  const authService = new AuthService(
    log,
    natsConfig?.audience || natsConfig.natsServers[0],
    natsConfig?.privateKey,
    natsConfig?.publicKey,
  );

  const permissions = {
    publish: {
      allow: ['baseline.>'],
    },
    subscribe: {
      allow: [`baseline.inbound`],
    },
  };

  return await authService.vendBearerJWT(
    subject,
    5000,
    permissions,
  );
};
